mapper.update:
UPDATE USER_ARTWORK
SET TITLE = #{title},
    DESCRIPTION = #{description},
    IMAGEPATH = #{imagePath},
    PRICE = #{price}
WHERE ARTID = #{artId}

UPDATE USER_ARTWORK
SET BUYERNAME = #{buyerName}, STATUS = '판매완료'
WHERE ARTID = #{artId}

UPDATE USER_INFO
SET PROFILEIMAGE = #{profileImgPath}
WHERE USERNAME = #{userName}

mapper.select:
SELECT PROFILEIMAGE
FROM USER_INFO
WHERE USERNAME = #{userName}

mapper.update:
UPDATE USER_INFO
SET PROFILEIMAGE = NULL
WHERE USERNAME = #{userName}
