mapper.select:
SELECT USERNAME, NICKNAME, PASSWORD, EMAIL, STATUS, PHONE
FROM USER_INFO 
WHERE USERNAME = #{userName}

SELECT COUNT(*) 
FROM USER_INFO
WHERE USERNAME = #{userName}

SELECT COUNT(*)
FROM USER_INFO
WHERE EMAIL = #{email}

mapper.update:
UPDATE USER_INFO 
SET STATUS = 'Y' 
WHERE EMAIL = #{email}

UPDATE USER_INFO
SET EMAIL = #{email}
WHERE USERNAME = #{userName}

UPDATE USER_INFO
SET PASSWORD = #{password},
EMAIL = #{email},
STATUS = #{status}
WHERE USERNAME = #{userName}

mapper.delete:
DELETE FROM USER_INFO 
WHERE USERNAME = #{userName}
AND STATUS = 'Y'
