mapper.update:
UPDATE USER_MESSAGE 
SET READYN = 'Y',
    READDATE = SYSDATE
WHERE PRIVATEMSGNO = #{privateMsgNo}
  AND RECEIVERUSERNAME = #{receiverUserName}
  AND READYN = 'N'

UPDATE USER_MESSAGE 
SET DELETESENDER = 'Y'
WHERE PRIVATEMSGNO = #{privateMsgNo}

UPDATE USER_MESSAGE 
SET DELETERECEIVER = 'Y'
WHERE PRIVATEMSGNO = #{privateMsgNo}
