mapper.update:
UPDATE USER_MESSAGE 
SET READYN = 'Y',
    READDATE = SYSDATE
WHERE PRIVATEMSGNO = #{privateMsgNo}
  AND RECEIVERUSERNAME = #{receiverUserName}
  AND READYN = 'N'

UPDATE USER_MESSAGE 
SET DELETESENDER = 'Y'
WHERE PRIVATEMSGNO = #{privateMsgNo}

UPDATE USER_MESSAGE 
SET DELETERECEIVER = 'Y'
WHERE PRIVATEMSGNO = #{privateMsgNo}

mapper.select:
SELECT * FROM ( SELECT ROWNUM as rn, sub.* FROM ( SELECT m.PRIVATEMSGNO, m.SENDERUSERNAME, m.RECEIVERUSERNAME, m.TITLE, m.CONTENT, m.SENDDATE, m.READDATE, m.READYN, m.DELETESENDER, m.DELETERECEIVER, m.MESSAGETYPE, m.PARENTPRIVATEMSGNO, s.NICKNAME AS SENDER_NICKNAME, r.NICKNAME AS RECEIVER_NICKNAME FROM USER_MESSAGE m LEFT JOIN USER_INFO s ON m.SENDERUSERNAME = s.USERNAME LEFT JOIN USER_INFO r ON m.RECEIVERUSERNAME = r.USERNAME
AND (m.SENDERUSERNAME LIKE '%' || #{senderUserName} || '%' OR s.NICKNAME LIKE '%' || #{senderUserName} || '%')
AND (m.RECEIVERUSERNAME LIKE '%' || #{receiverUserName} || '%' OR r.NICKNAME LIKE '%' || #{receiverUserName} || '%')
AND m.TITLE LIKE '%' || #{title} || '%'
AND m.MESSAGETYPE = #{messageType}
AND m.READYN = #{readYn}
ORDER BY m.SENDDATE DESC ) sub
WHERE ROWNUM <= #{offset} + #{size}
)
WHERE rn > #{offset}

SELECT COUNT(*) FROM USER_MESSAGE m LEFT JOIN USER_INFO s ON m.SENDERUSERNAME = s.USERNAME LEFT JOIN USER_INFO r ON m.RECEIVERUSERNAME = r.USERNAME
AND (m.SENDERUSERNAME LIKE '%' || #{senderUserName} || '%' OR s.NICKNAME LIKE '%' || #{senderUserName} || '%')
AND (m.RECEIVERUSERNAME LIKE '%' || #{receiverUserName} || '%' OR r.NICKNAME LIKE '%' || #{receiverUserName} || '%')
AND m.TITLE LIKE '%' || #{title} || '%'
AND m.MESSAGETYPE = #{messageType}
AND m.READYN = #{readYn}

